import React, { useState } from 'react';
import { apiService } from 'services/apiService';
import FormCard from 'components/commons/FormCard';
import InputField from 'components/commons/InputField';
import { Users, User, Building, KeyRound } from 'lucide-react';
import 'assets/styles/Form.css';

const RegisterVisitorPage = () => {
    const [formData, setFormData] = useState({ name: '', reason: '', validUntil: '' });
    const [isLoading, setIsLoading] = useState(false);
    const [message, setMessage] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({...prev, [name]: value}));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setMessage(null);
        try {
            await apiService.addVisitor(formData);
            setMessage({ type: 'success', text: 'Visitante registrado con éxito.' });
            setFormData({ name: '', reason: '', validUntil: '' });
        } catch(err) {
            setMessage({ type: 'error', text: 'Error al registrar al visitante.' });
        } finally {
            setIsLoading(false);
            setTimeout(() => setMessage(null), 5000);
        }
    };

    return (
        <div className="page-container">
            <FormCard title="Alta de Visitantes Externos" icon={Users}>
                <form onSubmit={handleSubmit} className="form-body">
                    <InputField id="name" label="Nombre Completo del Visitante" type="text" placeholder="Nayeli Corona" icon={User} value={formData.name} onChange={handleChange} />
                    <InputField id="reason" label="Motivo de la Visita" type="text" placeholder="Conferencia de Tecnología" icon={Building} value={formData.reason} onChange={handleChange} />
                    <InputField id="validUntil" label="Acceso Válido Hasta" type="date" placeholder="" icon={KeyRound} value={formData.validUntil} onChange={handleChange} />

                    {message && (
                        <div className={`form-message ${message.type}`}>
                            {message.text}
                        </div>
                    )}

                    <div className="form-footer">
                        <button type="submit" disabled={isLoading} className="submit-button">
                            {isLoading ? 'Registrando...' : 'Registrar Visitante'}
                        </button>
                    </div>
                </form>
            </FormCard>
        </div>
    );
};

export default RegisterVisitorPage;
